import styled from "styled-components";
import { Link } from "react-router-dom";

export const StyleHeaderContainer = styled.div`
  height: 70px;
  width: 100%;
  display: flex;
  justify-content: space-between;
  margin-bottom: 25px;
  position: relative;
`;

export const StyleLogoContainer = styled(Link)`
  height: 100%;
  width: 70px;
  padding: 10px;
`;

export const StyleOptionsContainer = styled.div`
  width: 50%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: flex-end;
`;

const StyledLink = styled(Link)`
  padding: 10px 15px;
  cursor: pointer;

  &:hover {
    border-bottom: 1px solid black;
  }

  &.selected {
    border-color: rgba(175, 47, 47, 0.2);
  }
`;

export const OptionLink = ({ activeClassName, ...props }) => (
  <StyledLink {...props} activeClassName={`selected ${activeClassName}`} />
);

export default FilterLink;
