import CartActionTypes from "./cart.types";

const INITIAL_STATES = {
  hidden: true
};

const CartReducer = (state = INITIAL_STATES, action) => {
  switch (action.types) {
    case CartActionTypes.TOGGLE_CART_HIDDEN:
      return {
        ...state,
        hidden: !state.hidden
      };
    default:
      return state;
  }
};

export default CartReducer;
